import React, { useEffect } from 'react'

function Expericenebox() {



const startcount =()=>{
  const counters = document.querySelectorAll('.value');
  const speed = 200;

  counters.forEach( counter => {
    const animate = () => {
       const value = +counter.getAttribute('akhi');
       const data = +counter.innerText;
      
       const time = value / speed;
      if(data < value) {
           counter.innerText = Math.ceil(data + time);
           setTimeout(animate, 300);
         }else{
           counter.innerText = value;
         }
      
    }
    
    animate();
 });
 

}

useEffect(()=>{
 startcount()
},[])




    return (
        <div className="team-experience p-1 p-sm-3" data-aos="fade-up"
        data-aos-duration="1000">
        <div className="row">
          <div className="col-lg-8 col-md-6 col-sm-6 col-12">
            <div className="ex-box">
              <div className="exp-tit ">
                <h6>Hours Behind the Lens</h6>
              </div>
              <div className="exp-count" >
                <h2 className="value" akhi="2000000" >
                  0<span>K+</span>
                </h2>
              </div>
            </div>
          </div>
          <div className="col-lg-4 col-md-6 col-sm-6 col-12">
            <div className="ex-box">
              <div className="exp-tit">
                <h6>Years of Experience</h6>
              </div>
              <div className="exp-count">
                <h2 >
                  15<span>+</span>
                </h2>
              </div>
            </div>
          </div>
          <div className="col-lg-4 col-md-6 col-sm-6 col-12">
            <div className="ex-box">
              <div className="exp-tit">
                <h6>Awards and Recognitions</h6>
              </div>
              <div className="exp-count">
                <h2>
                  13<span>+</span>
                </h2>
              </div>
            </div>
          </div>
          <div className="col-lg-8 col-md-6 col-sm-6 col-12">
            <div className="ex-box">
              <div className="exp-tit">
                <h6>Happy Clients Served</h6>
              </div>
              <div className="exp-count">
                <h2>
                  200<span>+</span>
                </h2>
              </div>
            </div>
          </div>
        </div>
        {/* <div class="content">
  <div class="value" akhi="1000">0</div>
