import React from 'react'

function Ourworks() {

    let carousel = document.querySelector('.carousel');
    let cellCount = 5;
    let selectedIndex = 0;
    
    function rotateCarousel() {
      let angle = selectedIndex / cellCount * -360;
      carousel.style.transform = 'translateZ(-275px) rotateY(' + angle + 'deg)';
    }
    
    let prevButton = document.querySelector('.previous-button');
    prevButton.addEventListener( 'click', function() {
      selectedIndex--;
      rotateCarousel();
    });
    
    let nextButton = document.querySelector('.next-button');
    nextButton.addEventListener( 'click', function() {
      selectedIndex++;
      rotateCarousel();
    });
    
    //--------------//
    // IMAGE TOGGLE //
    //--------------//
    function changeImage() {
    // alert("click!");
        let x = document.getElementById("image");
        let y = document.getElementById("imageChangeButton");
        let cat_url = "https://cdn.shopify.com/s/files/1/1453/9306/products/19950NBT2-2_1024x1024.jpg?v=1504778561";
        let dog_url = "https://i.ebayimg.com/images/g/0z8AAOSwFe5Xz-p2/s-l300.jpg";
        if(x.src === cat_url){
          x.src = dog_url;
          y.innerText = "Change to Cat";
        } else {
          x.src = cat_url;
          y.innerText = "Change to Dog";
        }
    }

    return (
        <>
          <div class="wrapper">
    <div class="object">
      <div class="carousel">
        <div class="cell">
          <h3>COOL CAT</h3>
          <img id="image" src="https://cdn.shopify.com/s/files/1/1453/9306/products/19950NBT2-2_1024x1024.jpg?v=1504778561"/>
        </div>
        <div class="cell">
          <h3 style="color:black;">COOL EYES</h3>
          <img id="image" src="http://s1.1zoom.me/big0/301/Eyes_Painting_Art_Eyelash_lash_Glance_White_522554_1024x1024.jpg"/>
        </div>
        <div class="cell">
          <h3>LET'S GET WIRED</h3>
          <img id="image" src="https://images-na.ssl-images-amazon.com/images/I/71Sozrhp53L._SX679_.jpg"/>
        </div>
        <div class="cell">
          <h3>DEAD POOL</h3>
          <img id="image" src="http://www.honcho-sfx.com/blog/wp-content/uploads/2015/08/Deadpool-trailer-300x300.jpg"/>
        </div>
        <div class="cell">
          <h3>MOON</h3>
          <img id="image" src="http://www.filipaveiga.com/Portals/10/EasyDNNnews/1284/1284fullmoon_lick_big-640x640.jpg"/>
        </div>
     </div>
    </div>
  </div>

    <p class="button-group">
      <button class="previous-button">Previous</button>
      <button class="next-button">Next</button>
    </p>
  

        </>
    )
}

export default Ourworks
