import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Mainroutes from "./Routes/Mainroutes";
import AOS from "aos";
import "aos/dist/aos.css";
import { useEffect } from "react";


function App() {

   useEffect(() => {
    AOS.init({
      startEvent: 'load', // You can customize when to start AOS
      disable: 'mobile',  // Disable on mobile (optional)
    });

    // Optional: Add custom event to stop animation when scrolled
    window.addEventListener('scroll', function () {
      const elements = document.querySelectorAll('.aos-animate');
      elements.forEach(element => {
        const rect = element.getBoundingClientRect();
        if (rect.top < 0 || rect.bottom > window.innerHeight) {
          element.style.visibility = 'hidden'; // Hide element when out of view
        } else {
          element.style.visibility = 'visible'; // Show element when in view
        }
      });
    });
  }, []);

  return (
    <> 
         <Mainroutes/>
    </>
  );
}

export default App;
