import React, { useEffect, useState } from 'react'
import axios from 'axios'
function Carrer() {
    const[id,setid]=useState('')
    const[title,settitle]=useState('')
    const[body,setbody]=useState('')
    const[getdata,setgetdata]=useState([])
    const getting=async()=>{
          await axios.get('https://jsonplaceholder.typicode.com/posts')
          .then((res)=>{
            setgetdata(res.data)
          })
     
    }


    useEffect(()=>{
        getting()
    },[])

  
    const submiteddata=async(e)=>{
        e.preventDefault()
           await axios.post('https://jsonplaceholder.typicode.com/posts',{id,title,body})
           .then((res)=>{
            setgetdata([...getdata, res.data])
           })

    }
    
    const del=async(id)=>{
        await axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`)
        .then((res)=>{
            console.log(res)
        
        })
    }
    return (
        <>
        <div className="post-data">
            <div className="conatiner">
                <div className="form">
                        <form onSubmit={submiteddata}>
                    <div className="title">
                        <input type='text' placeholder='title' onChange={(e)=>{settitle(e.target.value)}}/>
                    </div>
                    <div className="body">
                        <input type='text' placeholder='boby' onChange={(e)=>{setbody(e.target.value)}}/>
                    </div>
                   <div className="ok-button">
                   <button className='btn btn-primary'>Submit</button>
                   </div>
                        </form>
                </div>
            </div>
        </div>
        <div className="apicollection">
            <div className="container">
                <div className="row">
                      {
                        getdata.map((r,index)=>{
                            return(
                                <div className="col-md-4" key={index}>
                                
                                    <div className="id">
                                        {r.id}
                                    </div>
                                    <div className="name">
                                        {r.title}
                                    </div>
                                    <div className="body">
                                        {r.body}
                                    </div>
                                    <div className="delete">
                                        <button  onClick={()=>del(r.id)}>delete</button>
                                    </div>
                                </div>
                            )
                        })
                      }
                </div>
            </div>
        </div>
    
        </>
    )
}

export default Carrer
